import React from 'react';
import debounce from 'lodash/debounce';
import { useDispatch } from 'react-redux';
import { fetchCities } from '../features/citySlice';

const SearchBar = ({ query, setQuery, handleCitySelect, cities }) => {
  const dispatch = useDispatch();

  const debounceCall = debounce(() => {
    console.log('debounced fetch cities');
    dispatch(fetchCities(query))
  }, 1000);

  const handleInputChange = (e) => {
    const value = e.target.value;
    setQuery(() => {
      // מפעילים חיפוש אחרי 2 תווים
      if (value.length > 2) {
        console.log('fetch cities', query);
        debounceCall();
      }
      return value;
    });
  };

  return (
    <div>
      <input
        type="text"
        value={query}
        onChange={handleInputChange}
        placeholder="Search for a city"
      />
      {cities.length > 0 && (
        <ul>
          {cities.map((city) => (
            <li key={city.Key} onClick={() => handleCitySelect(city)}>
              {city.LocalizedName}, {city.Country.ID}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SearchBar;
