import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFavorite } from '../features/favoritesSlice';
import { fetchWeather } from '../features/currentWeatherSlice';

const FavoritesPage = () => {
  const favorites = useSelector((state) => state.favorites.items);
  const [citiesWeather, setCitiesWeather] = React.useState([]);
  const dispatch = useDispatch();

  const handleRemove = (city) => {
    dispatch(removeFavorite(city));
  };

  // eslint-disable-next-line react-hooks/exhaustive-deps
  useEffect(async () => {
      const promises = favorites.map((city) =>
        fetchWeather(city.Key).then((data) => ({ ...data, city }))
      );
      const data = await Promise.all(promises);
      setCitiesWeather(data);
  });

  return (
    <div>
      <h1>Favorites</h1>
      {favorites.length > 0 ? (
        <ul>
          {favorites.map((city) => (
            <li key={city.Key}>
              {city?.AdministrativeArea?.CountryID}, {city?.AdministrativeArea?.EnglishName}
              <button onClick={() => handleRemove(city)}>Remove</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No favorites added yet.</p>
      )}
    </div>
  );
};

export default FavoritesPage;
